function showSuccessMessage(message) {
    $('.toast').removeClass('bg-warning');
    $('.toast').removeClass('bg-danger');
    $('.toast').addClass('bg-success');
    $('.toast-msg').html('<i class="fa fa-check" aria-hidden="true"></i> ' + message);
    $('.toast').toast('show');
}



function showWarningMessage(message) {
    $('.toast').removeClass('bg-success');
    $('.toast').removeClass('bg-danger');
    $('.toast').addClass('bg-warning');
    $('.toast-msg').html('<i class="fa fa-exclamation-triangle" aria-hidden="true"></i> ' + message);
    $('.toast').toast('show');
}



function showErrorMessage(message) {
    $('.toast').removeClass('bg-success');
    $('.toast').removeClass('bg-warning');
    $('.toast').addClass('bg-danger');
    $('.toast-msg').html('<i class="fa fa-times-circle" aria-hidden="true"></i> ' + message);
    $('.toast').toast('show');
}